section.script:
      $(document).ready( function() {
        $("#jki_statistic").empty();
        var year=new Date().getFullYear();
        var year1=year-5;
        if (year1 < 2018) year1 = 2018;
        for (i = year1; i <= year; i++) {
          var li = $('
');
          li.append ('' + i + '');
          $("#jki_statistic").append(li); 
          var li = $('
');
          li.append ('' + i + ' mit JCR vom Vorjahr');
          $("#jki_statistic").append(li);
          li = $('
');
          li.append ('' + i + ' mit JCR von 2 Jahren zuvor');
          $("#jki_statistic").append(li);
        }

        for (i = year1; i <= year; i++) {
          var li = $('
');
          li.append ('' + i + '');
          $("#jki_statistic_2020").append(li); 
          var li = $('
');
          li.append ('' + i + ' mit JCR vom Vorjahr');
          $("#jki_statistic_2020").append(li);
          li = $('
');
          li.append ('' + i + ' mit JCR von 2 Jahren zuvor');
          $("#jki_statistic_2020").append(li);
        }
      });
