function createAffiQuery (institutes) {
  var query = "";
  $.each( institutes, function ( i, institut) {
    query += 'mods.affiliation:"' + institut + '" OR ';
  });
  query = query.substring(0, query.length -3);
  query = "(" + query + ")" ; 
  return query;
}

var searchParams = new URLSearchParams(window.location.search);
var year="";
if (searchParams.has("year") === true) {
  year=searchParams.get("year");
} else {
  year=new Date().getFullYear();
}
var JCRClasses1Yb=false;
if (searchParams.has("JCRClasses1Yb") === true && searchParams.get("JCRClasses1Yb") == 'true' ) {
  JCRClasses1Yb=true;
}
var JCRClasses2Yb=false;
if (searchParams.has("JCRClasses2Yb") === true && searchParams.get("JCRClasses2Yb") == 'true' ) {
  JCRClasses2Yb=true;
} 

$(document).ready( function() {
  $('h1').text(" Statistik - JKI Jahresstatistik " + year);
  var year_jcr = year;
  if (JCRClasses1Yb) year_jcr = year -1;
  if (JCRClasses2Yb) year_jcr = year -2;
  $('#text_jcr').text(" mit den IF von (" + year_jcr + ")");
});

